mapper.insert:
INSERT INTO
drole_address(

)
VALUES (#{id}, #{dRoleId}, #{addressId},#{dRoleName});

    select
    LAST_INSERT_ID() as id

mapper.update:
UPDATE drole_address

    DROLEID=#{dRoleId},
    ADDRESSID=#{addressId},
    DROLENAME=#{dRoleName}

WHERE ID = #{id};

mapper.delete:
DELETE FROM drole_address

    ID=#{id}
    AND DROLEID=#{dRoleId}

mapper.select:

SELECT a.id,a.province,round(avg(r.riskvalue)) as avgRiskValue,count(DISTINCT(cr.reportnumber)) as craneNumber,col.riskcolor as color
FROM craneinspectreport cr,riskvalue r,address a,riskcolor col,user u,drole dr,user_drole udr,drole_address draddr
where

    u.id=#{userId}


    and cr.equipmentvariety=#{equipmentVariety}


    and ROUND(DATEDIFF(NOW(),cr.manufacturedate)/365) between #{sTime}


    and #{eTime}


    AND r.riskvalue BETWEEN #{startValue}


    AND #{endValue}  AND

cr.reportnumber=r.reportnumber AND col.riskvalue=r.riskvalue AND u.id=udr.userId AND udr.droleId=dr.id AND draddr.droleId=dr.id AND draddr.addressId=a.id
AND a.id=cr.addressId
GROUP BY a.province order by craneNumber desc

SELECT addressId as id,city,avgRiskValue,craneNumber,color
FROM  city_riskvalue_temp
WHERE userId=#{userId} and province=#{province}

 SELECT addressId as id,area,avgRiskValue,craneNumber,color
FROM area_riskvalue_temp
WHERE userId=#{userId} and province=#{province} and city=#{city}

mapper.update:
UPDATE
province_riskvalue
SET

 riskvalue=#{riskValue}


 WHERE province=#{province}
