mapper.insert:
INSERT INTO
installation(

)
VALUES (#{id}, #{contractId},#{type},#{installDeviceId},#{installMan},#{installTime},#{installStatus},#{image},#{appId});

    select
    LAST_INSERT_ID() as id

mapper.select:
select id
from installation
where INSTALLDEVICEID=#{installDeviceId}

select installation.*,contract.name,contract.customerName
from installation,contract,device
where installation.contractId=contract.id and installation.installDeviceId=device.id
 AND installation.APPID=#{appId}
AND installation.installMan like CONCAT('%',#{user},'%') 
AND device.name like CONCAT('%',#{device},'%')
 AND installation.installTime>=#{startTime}
 AND installation.installTime<=#{endTime}

select c.name contractName,i.type,d.name deviceName,i.installMan,i.installTime,i.installStatus
from  installation AS i,contract AS c,device AS d
where i.appId=#{appId} AND i.contractId=c.id AND i.installDeviceId=d.id
AND c.name LIKE #{contractName}
AND d.name LIKE #{deviceName}
